                vc.FechaNacimiento = Persona.FechaNacimiento.Substring(0,10);
                vc.FechaPdosis = DateTime.Now.ToString("yyyy-MM-dd");


                //Cargando en variables los contenidos de la fecha.
                MesString =Persona.FechaNacimiento.Substring(5,2);
