    // Metodo para registrar registrados.
    void AddPerson(){
        using (Tarea8Context cmd_Insert = new Tarea8Context())
        {
            if(vc.Cedula == null){
                Mensaje="Debe Lenar el Campo Cedula!";

            }
            else{
                cmd_Insert.Add(vc);
                RestarVacuna();
                cmd_Insert.SaveChanges();
                Clear();
                Mensaje="Paciente Vacunado!";
            }
        }
    }

    // Metodo para limpiar los campos.
    void Clear(){
        vc.Cedula = null;
        vc.Nombre = "";
        vc.Apellido = "";
